mapper.select:
SELECT count(*) 
FROM LIKEY
WHERE IDEA_SEQ = #{idea_seq}

mapper.update:

UPDATE IDEA 
SET project_name=#{project_name}, short_description=#{short_description}, category=#{category} 
WHERE idea_seq=#{idea_seq}


UPDATE GOODS 
SET open_status=#{open_status}, content=#{content}, price=#{price} 
WHERE goods_seq=#{goods_seq}
